(function() {
  'use strict';

  angular.module('encurtadorUrl',
  [
    'ui.router',
    'ngAnimate',
    'typer',
    'angular-loading-bar'
  ]);


}());

(function() {
  'use strict';

  angular.module('encurtadorUrl')
    .config(routesConfig);

  function routesConfig($stateProvider, $urlRouterProvider) {

    $urlRouterProvider.otherwise("/home");

    $stateProvider
      .state('app', {
        abstract: true,
        views: {
          "conteudo": {
            templateUrl: "home/home.html"
          }
        }
      })
      .state('app.home', {
        url: "/home",
        views: {
          "conteudo@": {
            templateUrl: "home/home.html",
            controller: "HomeController",
            controllerAs: "home"
          }
        }
      });
  }

}());

(function() {
    'use strict';

    angular
        .module('encurtadorUrl')
        .controller('HomeController', HomeController);

    HomeController.$inject = [];

    /* @ngInject */
    function HomeController() {
        var vm = this;

        activate();

        function activate() {
          
        }
    }
})();
